import "./DashboardCards.css";
import { getCategory } from "../utils/cropCategories";

function DashboardCards({ data = [] }) {

  console.log("Dashboard Data:", data);
  const categories = {
    Vegetables: [],
    Fruits: [],
    Others: []
  };

  data.forEach(item => {
    const cropName =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    const category = getCategory(cropName);
    categories[category].push(item);
  });

  return (
    <div className="dashboard-wrapper">
      <h2 className="dashboard-title">üåæ Crop Categories</h2>

      <div className="card-container">
        <div className="card green">
          <h3>ü•¶ Vegetables</h3>
          <h1>{categories.Vegetables.length}</h1>
          <p>Crops</p>
        </div>

        <div className="card orange">
          <h3>üçé Fruits</h3>
          <h1>{categories.Fruits.length}</h1>
          <p>Crops</p>
        </div>

        <div className="card blue">
          <h3>üåæ Others</h3>
          <h1>{categories.Others.length}</h1>
          <p>Crops</p>
        </div>
      </div>
    </div>
  );
}

export default DashboardCards;
