import { useState } from "react";
import "./PriceAlertSettings.css";

function PriceAlertSettings({ crops = [] }) {
  const [crop, setCrop] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const handleSave = () => {
    if (!crop) {
      alert("Please select a crop");
      return;
    }

    const alerts =
      JSON.parse(localStorage.getItem("priceAlerts")) || {};

    alerts[crop] = {
      min: Number(minPrice),
      max: Number(maxPrice)
    };

    localStorage.setItem(
      "priceAlerts",
      JSON.stringify(alerts)
    );

    alert("âœ… Price alert saved");
  };

  return (
    <div className="price-alert-card">
      <h3>ðŸ”” Price Alert Settings</h3>

      <select
        value={crop}
        onChange={e => setCrop(e.target.value)}
      >
        <option value="">ðŸŒ¾ Select Crop</option>

        {crops.map((c, i) => (
          <option key={i} value={c}>
            {c}
          </option>
        ))}
      </select>

      <input
        type="number"
        placeholder="Notify if price BELOW"
        value={minPrice}
        onChange={e => setMinPrice(e.target.value)}
      />

      <input
        type="number"
        placeholder="Notify if price ABOVE"
        value={maxPrice}
        onChange={e => setMaxPrice(e.target.value)}
      />

      <button onClick={handleSave}>
        ðŸ’¾ Save Alert
      </button>
    </div>
  );
}

export default PriceAlertSettings;
