import { useState } from "react";
import "./PriceAlertSettings.css";

function PriceAlertSettings({ crops = [] }) {
  const [crop, setCrop] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const handleSave = () => {
    if (!crop) {
      alert("Please select a crop");
      return;
    }

    const alertData = {
      crop,
      minPrice,
      maxPrice
    };

    localStorage.setItem(
      "priceAlert",
      JSON.stringify(alertData)
    );

    alert("âœ… Price alert saved successfully!");
  };

  return (
    <div className="alert-card">
      <h3>ðŸ”” Price Alert Settings</h3>

      <select value={crop} onChange={e => setCrop(e.target.value)}>
        <option value="">ðŸŒ¾ Select Crop</option>
        {crops.map(c => (
          <option key={c} value={c}>
            {c}
          </option>
        ))}
      </select>

      <input
        type="number"
        placeholder="ðŸ“‰ Alert when price below (â‚¹)"
        value={minPrice}
        onChange={e => setMinPrice(e.target.value)}
      />

      <input
        type="number"
        placeholder="ðŸ“ˆ Alert when price above (â‚¹)"
        value={maxPrice}
        onChange={e => setMaxPrice(e.target.value)}
      />

      <button onClick={handleSave}>
        Save Alert
      </button>

      <p className="note">
        ðŸ”” You will be notified when market price goes high or low
      </p>
    </div>
  );
}

export default PriceAlertSettings;
