function DashboardCards({ data = [], onCategoryClick }) {
  // SAFE GUARD
  if (!onCategoryClick) return null;

  const vegetablesCount = data.filter(d => {
    const name =
      d.Commodity ||
      d.commodity ||
      d.Crop ||
      d.crop_name ||
      "";
    return name.toLowerCase().includes("tomato") ||
           name.toLowerCase().includes("onion") ||
           name.toLowerCase().includes("chilli") ||
           name.toLowerCase().includes("potato");
  }).length;

  const fruitsCount = data.filter(d => {
    const name =
      d.Commodity ||
      d.commodity ||
      d.Crop ||
      d.crop_name ||
      "";
    return name.toLowerCase().includes("apple") ||
           name.toLowerCase().includes("banana") ||
           name.toLowerCase().includes("mango") ||
           name.toLowerCase().includes("orange");
  }).length;

  const othersCount = data.length - vegetablesCount - fruitsCount;

  return (
    <div style={{ padding: "20px" }}>
      <h2>üåæ Crop Dashboard</h2>

      <div style={{ display: "flex", gap: "20px" }}>
        {/* VEGETABLES */}
        <div
          onClick={() => onCategoryClick("Vegetables")}
          style={{
            cursor: "pointer",
            background: "#e8f5e9",
            padding: "20px",
            borderRadius: "15px",
            width: "250px",
            borderLeft: "6px solid green"
          }}
        >
          <h3>ü•¶ Vegetables</h3>
          <h1>{vegetablesCount}</h1>
          <p>Crops</p>
        </div>

        {/* FRUITS */}
        <div
          onClick={() => onCategoryClick("Fruits")}
          style={{
            cursor: "pointer",
            background: "#fff3e0",
            padding: "20px",
            borderRadius: "15px",
            width: "250px",
            borderLeft: "6px solid orange"
          }}
        >
          <h3>üçé Fruits</h3>
          <h1>{fruitsCount}</h1>
          <p>Crops</p>
        </div>

        {/* OTHERS */}
        <div
          onClick={() => onCategoryClick("Others")}
          style={{
            cursor: "pointer",
            background: "#e3f2fd",
            padding: "20px",
            borderRadius: "15px",
            width: "250px",
            borderLeft: "6px solid blue"
          }}
        >
          <h3>üì¶ Others</h3>
          <h1>{othersCount}</h1>
          <p>Crops</p>
        </div>
      </div>
    </div>
  );
}

export default DashboardCards;
