import { useState } from "react";
import { getCategory } from "../utils/cropCategories";

function CategoryList({ data, category }) {
  const [selectedCrop, setSelectedCrop] = useState(null);

  // 1ï¸âƒ£ Filter by category
  const filteredByCategory = data.filter(item => {
    const cropName =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    return getCategory(cropName) === category;
  });

  // 2ï¸âƒ£ Group by crop name
  const cropMap = {};

  filteredByCategory.forEach(item => {
    const cropName =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    if (!cropMap[cropName]) {
      cropMap[cropName] = [];
    }
    cropMap[cropName].push(item);
  });

  const cropNames = Object.keys(cropMap);

  return (
    <div style={{ padding: "20px" }}>
      <h2>{category} Crops</h2>

      {/* ðŸ”¹ CROP LIST */}
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        {cropNames.map(crop => (
          <button
            key={crop}
            onClick={() => setSelectedCrop(crop)}
            style={{
              padding: "10px 15px",
              borderRadius: "8px",
              border: "1px solid #ccc",
              cursor: "pointer",
              background:
                selectedCrop === crop ? "#c8e6c9" : "#f5f5f5"
            }}
          >
            {crop}
          </button>
        ))}
      </div>

      {/* ðŸ”¹ MARKET TABLE */}
      {selectedCrop && (
        <div style={{ marginTop: "30px" }}>
          <h3>{selectedCrop} â€“ Market Prices</h3>

          <table border="1" width="100%" cellPadding="8">
            <thead>
              <tr>
                <th>Market</th>
                <th>District</th>
                <th>State</th>
                <th>Min Price (â‚¹)</th>
                <th>Modal Price (â‚¹)</th>
                <th>Max Price (â‚¹)</th>
              </tr>
            </thead>

            <tbody>
              {cropMap[selectedCrop].map((item, index) => (
                <tr key={index}>
                  <td>{item.Market}</td>
                  <td>{item.District}</td>
                  <td>{item.State}</td>
                  <td>{item.Min_x0020_Price}</td>
                  <td>{item.Modal_x0020_Price}</td>
                  <td>{item.Max_x0020_Price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default CategoryList;
