import { useEffect, useState } from "react";

function PriceListPage() {
  const [data, setData] = useState([]); // âœ… must be array
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/data")
      .then(res => res.json())
      .then(json => {
        setData(json || []); // âœ… fallback
        setLoading(false);
      })
      .catch(() => {
        setData([]);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <h3 style={{ padding: "20px" }}>Loading prices...</h3>;
  }

  if (!data.length) {
    return <h3 style={{ padding: "20px" }}>No data available</h3>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“‹ Crop Price List</h2>

      <table border="1" width="100%" cellPadding="8">
        <thead>
          <tr>
            <th>Crop</th>
            <th>Market</th>
            <th>District</th>
            <th>State</th>
            <th>Min â‚¹</th>
            <th>Modal â‚¹</th>
            <th>Max â‚¹</th>
          </tr>
        </thead>

        <tbody>
          {data.map((item, i) => {
            const crop =
              item.Commodity ||
              item.commodity ||
              item.Crop ||
              item.crop_name;

            return (
              <tr key={i}>
                <td>{crop}</td>
                <td>{item.Market}</td>
                <td>{item.District}</td>
                <td>{item.State}</td>
                <td>{item.Min_x0020_Price}</td>
                <td>{item.Modal_x0020_Price}</td>
                <td>{item.Max_x0020_Price}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default PriceListPage;
