import { useState } from "react";
import { getCategory } from "../utils/cropCategories";


function CategoryList({ data, category }) {
  const [search, setSearch] = useState("");
  const [selectedCrop, setSelectedCrop] = useState(null);

  // 1Ô∏è‚É£ Filter by category
  const categoryData = data.filter(item => {
    const crop =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    return getCategory(crop) === category;
  });

  // 2Ô∏è‚É£ Group by crop name
  const cropMap = {};
  categoryData.forEach(item => {
    const crop =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    if (!cropMap[crop]) {
      cropMap[crop] = [];
    }
    cropMap[crop].push(item);
  });

  // 3Ô∏è‚É£ Search filter
  const cropNames = Object.keys(cropMap).filter(crop =>
    crop.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>{category} Crops</h2>

      {/* üîç SEARCH BAR */}
      <input
        type="text"
        placeholder="Search crop (e.g., Tomato)"
        value={search}
        onChange={e => {
          setSearch(e.target.value);
          setSelectedCrop(null);
        }}
        style={{
          padding: "10px",
          width: "300px",
          marginBottom: "15px",
          borderRadius: "8px",
          border: "1px solid #ccc"
        }}
      />

      {/* üåæ CROP LIST */}
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        {cropNames.map(crop => (
          <button
            key={crop}
            onClick={() => setSelectedCrop(crop)}
            style={{
              padding: "10px 14px",
              borderRadius: "20px",
              border: "1px solid #aaa",
              cursor: "pointer",
              background:
                selectedCrop === crop ? "#c8e6c9" : "#f5f5f5"
            }}
          >
            {crop}
          </button>
        ))}
      </div>

      {/* üìä MARKET TABLE */}
      {selectedCrop && (
        <div style={{ marginTop: "30px" }}>
          <h3>{selectedCrop} ‚Äì Market Prices</h3>

          <table border="1" width="100%" cellPadding="8">
            <thead>
              <tr>
                <th>Market</th>
                <th>District</th>
                <th>State</th>
                <th>Min ‚Çπ</th>
                <th>Modal ‚Çπ</th>
                <th>Max ‚Çπ</th>
              </tr>
            </thead>

            <tbody>
              {selectedCrop &&
  cropMap[selectedCrop] &&
  cropMap[selectedCrop].map((item, i) => (
    <tr key={i}>
      <td>{item.Market}</td>
      <td>{item.District}</td>
      <td>{item.State}</td>
      <td>{item.Min_x0020_Price}</td>
      <td>{item.Modal_x0020_Price}</td>
      <td>{item.Max_x0020_Price}</td>
    </tr>
  ))
}

            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default CategoryList;
