import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
} from "chart.js";

import { Line } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

function PriceTrendGraph({ cropName, cropData = [] }) {
  if (!cropData.length) return null;

  const labels = cropData.map(
    (d, i) => d.Market || `Market ${i + 1}`
  );

  const prices = cropData.map(d =>
    Number(d.Modal_x0020_Price)
  );

  const data = {
    labels,
    datasets: [
      {
        label: "Modal Price (â‚¹)",
        data: prices,
        borderColor: "#2e7d32",
        backgroundColor: "rgba(46,125,50,0.2)",
        tension: 0.3
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: true
      }
    }
  };

  return (
    <div style={{ marginTop: "30px" }}>
      <h3>ðŸ“ˆ Price Trend â€“ {cropName}</h3>
      <Line data={data} options={options} />
    </div>
  );
}

export default PriceTrendGraph;
