import { useEffect, useState, useRef } from "react";
import DashboardCards from "../components/DashboardCards";
import CategoryList from "../components/CategoryList";
import TopBestCrops from "../components/TopBestCrops";
import FavoriteCrops from "../components/FavoriteCrops";
import { speakBestMarket } from "../utils/speakPrice";

function Dashboard() {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const hasSpoken = useRef(false);

  // üîÑ LOAD DATA + SAVE ALL CROPS
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/data")
      .then(res => res.json())
      .then(json => {
        setData(json);

        // ‚úÖ SAVE ALL UNIQUE CROPS
        const allCrops = [
          ...new Set(
            json
              .map(
                d =>
                  d.Commodity ||
                  d.commodity ||
                  d.Crop ||
                  d.crop_name
              )
              .filter(Boolean)
          )
        ];

        localStorage.setItem(
          "allCrops",
          JSON.stringify(allCrops)
        );
      })
      .catch(err => console.error(err));
  }, []);

  // ‚≠ê GET FAVORITES
  const favoriteCrops =
    JSON.parse(localStorage.getItem("favoriteCrops")) || [];

  // ‚≠ê SORT DATA ‚Üí FAVORITES FIRST
  const sortedData = [...data].sort((a, b) => {
    const cropA =
      a.Commodity || a.commodity || a.Crop || a.crop_name;
    const cropB =
      b.Commodity || b.commodity || b.Crop || b.crop_name;

    return (
      (favoriteCrops.includes(cropB) ? 1 : 0) -
      (favoriteCrops.includes(cropA) ? 1 : 0)
    );
  });

  // üîä MANUAL VOICE TRIGGER (Browser Safe)
  const handleSpeakBestMarket = () => {
    if (!sortedData.length) return;

    const hour = new Date().getHours();
    let greeting = "Good evening";
    if (hour < 12) greeting = "Good morning";
    else if (hour < 17) greeting = "Good afternoon";

    const best = sortedData.reduce((max, cur) =>
      Number(cur.Modal_x0020_Price) >
      Number(max.Modal_x0020_Price)
        ? cur
        : max
    );

    const crop =
      best.Commodity ||
      best.commodity ||
      best.Crop ||
      best.crop_name;

    speakBestMarket({
      crop,
      market: best.Market,
      state: best.State,
      price: best.Modal_x0020_Price,
      greeting
    });

    localStorage.setItem(
      "spokenDate",
      new Date().toDateString()
    );
    hasSpoken.current = true;
  };

  return (
    <div>
      {/* üîä VOICE ANNOUNCEMENT */}
      <div style={{ padding: "15px" }}>
        <button
          onClick={handleSpeakBestMarket}
          style={{
            padding: "12px 22px",
            fontSize: "16px",
            borderRadius: "8px",
            border: "none",
            background: "#2e7d32",
            color: "white",
            cursor: "pointer"
          }}
        >
          üîä Hear Today‚Äôs Best Market
        </button>
      </div>

      {/* DASHBOARD CARDS */}
      <DashboardCards
        data={sortedData}
        onCategoryClick={setSelectedCategory}
      />

      {/* ‚≠ê FAVORITE CROPS */}
      <FavoriteCrops
        allCrops={
          JSON.parse(localStorage.getItem("allCrops")) || []
        }
      />

      {/* TOP BEST CROPS */}
      <TopBestCrops data={sortedData} />

      {/* CATEGORY DETAILS */}
      {selectedCategory && (
        <CategoryList
          data={sortedData}
          category={selectedCategory}
        />
      )}
    </div>
  );
}

export default Dashboard;
