import { useState } from "react";
import "./AccountPage.css";
import PriceAlertSettings from "../components/PriceAlertSettings";

function AccountPage() {
  // üë§ USER DATA
  const storedUser = JSON.parse(localStorage.getItem("user")) || {};
  const [user] = useState(storedUser);

  // üì∏ PROFILE PHOTO
  const [photo, setPhoto] = useState(
    localStorage.getItem("profilePhoto")
  );

  const handlePhotoUpload = e => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      localStorage.setItem("profilePhoto", reader.result);
      setPhoto(reader.result);
    };
    reader.readAsDataURL(file);
  };

  // üö™ LOGOUT
  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  // üåæ SAFELY GET CROPS FOR ALERT SETTINGS
  const crops = (() => {
    try {
      const saved = JSON.parse(localStorage.getItem("allCrops"));
      return Array.isArray(saved)
        ? [...new Set(saved)].filter(Boolean)
        : [];
    } catch {
      return [];
    }
  })();

  return (
    <div className="account-page">
      <div className="account-card">
        <h2>üë§ My Account</h2>

        {/* üì∏ PROFILE PHOTO */}
        <div className="profile-section">
          <img
            src={
              photo ||
              "https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
            }
            alt="Profile"
            className="profile-pic"
          />

          <label className="upload-btn">
            Change Photo
            <input
              type="file"
              accept="image/*"
              hidden
              onChange={handlePhotoUpload}
            />
          </label>
        </div>

        {/* ‚ÑπÔ∏è USER INFO */}
        <div className="info">
          <p><b>Name:</b> {user.name || "-"}</p>
          <p><b>Phone:</b> {user.phone || "-"}</p>
          <p><b>Email:</b> {user.email || "-"}</p>
          <p><b>Place:</b> {user.place || "-"}</p>
        </div>

        {/* üîî PRICE ALERT SETTINGS */}
        <PriceAlertSettings crops={crops} />

        <button className="logout-btn" onClick={handleLogout}>
          üö™ Logout
        </button>
      </div>
    </div>
  );
}

export default AccountPage;
