import { useEffect, useState } from "react";
import ComparisonChart from "../components/ComparisonChart";

function ComparisonPage() {
  const [data, setData] = useState([]);
  const [cropA, setCropA] = useState("");
  const [cropB, setCropB] = useState("");

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/data")
      .then(res => res.json())
      .then(json => setData(json));
  }, []);

  const crops = [
    ...new Set(
      data.map(
        d =>
          d.Commodity ||
          d.commodity ||
          d.Crop ||
          d.crop_name
      )
    )
  ];

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“ˆ Crop Price Comparison</h2>

      {/* DROPDOWNS */}
      <div style={{ display: "flex", gap: "20px" }}>
        <select
          value={cropA}
          onChange={e => setCropA(e.target.value)}
        >
          <option value="">Select Crop 1</option>
          {crops.map(c => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>

        <select
          value={cropB}
          onChange={e => setCropB(e.target.value)}
        >
          <option value="">Select Crop 2</option>
          {crops.map(c => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
      </div>

      {/* GRAPH */}
      <ComparisonChart
        cropA={cropA}
        cropB={cropB}
        data={data}
      />
    </div>
  );
}

export default ComparisonPage;
