import { useState } from "react";
import productGroups from "../utils/productGroups";

function ProductGroups({ crops = [] }) {
  const [selectedGroup, setSelectedGroup] = useState(null);

  const getGroupedCrops = () => {
    if (!selectedGroup) return [];

    const keywords = productGroups[selectedGroup];

    return crops.filter(crop => {
      if (!crop) return false;
      const name = crop.toLowerCase();
      return keywords.some(k => name.includes(k));
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“¦ Products (Easy for Farmers)</h2>

      {/* GROUP BUTTONS */}
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        {Object.keys(productGroups).map(group => (
          <button
            key={group}
            onClick={() => setSelectedGroup(group)}
            style={{
              padding: "10px 16px",
              borderRadius: "20px",
              border: "1px solid #aaa",
              cursor: "pointer",
              background:
                selectedGroup === group ? "#d1f2eb" : "#f5f5f5"
            }}
          >
            {group}
          </button>
        ))}
      </div>

      {/* CROPS */}
      {selectedGroup && (
        <div style={{ marginTop: "20px" }}>
          <h3>{selectedGroup}</h3>

          <div style={{ display: "flex", gap: "8px", flexWrap: "wrap" }}>
            {getGroupedCrops().length > 0 ? (
              getGroupedCrops().map((item, i) => (
                <span
                  key={i}
                  style={{
                    padding: "6px 12px",
                    borderRadius: "14px",
                    border: "1px solid #ccc",
                    background: "#fff"
                  }}
                >
                  {item}
                </span>
              ))
            ) : (
              <p>No data available</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductGroups;
