// üîä Speak normal price (used in Price List)
export function speakPrice({
  crop,
  market,
  price,
  state
}) {
  if (!("speechSynthesis" in window)) {
    alert("Voice not supported in this browser");
    return;
  }

  const text = `The price of ${crop} in ${market}, ${state} is rupees ${price}`;

  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = "en-IN";
  utterance.rate = 0.9;

  window.speechSynthesis.cancel();
  window.speechSynthesis.speak(utterance);
}

// ‚≠ê Speak BEST market (used in Dashboard)
export function speakBestMarket({
  crop,
  market,
  price,
  state
}) {
  if (!("speechSynthesis" in window)) {
    return;
  }

  const text = `Best market today is ${market} in ${state}. ${crop} price is rupees ${price}`;

  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = "en-IN";
  utterance.rate = 0.9;

  window.speechSynthesis.cancel();
  window.speechSynthesis.speak(utterance);
}
