import "./DashboardCards.css";
import { getCategory } from "../utils/cropCategories";

function DashboardCards({ data = [], onCategoryClick }) {
  const categories = {
    Vegetables: 0,
    Fruits: 0,
    Others: 0
  };

  data.forEach(item => {
    const crop =
      item.Commodity ||
      item.commodity ||
      item.Crop ||
      item.crop_name;

    const category = getCategory(crop);
    if (categories[category] !== undefined) {
      categories[category]++;
    }
  });

  const handleClick = category => {
    // ‚úÖ SAFETY CHECK
    if (typeof onCategoryClick === "function") {
      onCategoryClick(category);
    }
  };

  return (
    <div className="dashboard-wrapper">
      <h2 className="dashboard-title">üåæ Crop Dashboard</h2>

      <div className="card-container">
        <div
          className="card green"
          onClick={() => handleClick("Vegetables")}
        >
          <h3>ü•¶ Vegetables</h3>
          <h1>{categories.Vegetables}</h1>
          <p>Crops</p>
        </div>

        <div
          className="card orange"
          onClick={() => handleClick("Fruits")}
        >
          <h3>üçé Fruits</h3>
          <h1>{categories.Fruits}</h1>
          <p>Crops</p>
        </div>

        <div
          className="card blue"
          onClick={() => handleClick("Others")}
        >
          <h3>üåæ Others</h3>
          <h1>{categories.Others}</h1>
          <p>Crops</p>
        </div>
      </div>
    </div>
  );
}

export default DashboardCards;
