import { useState } from "react";
import "./PriceAlertSettings.css";

function PriceAlertSettings({ crops = [] }) {
  const [crop, setCrop] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const cleanCrops = crops.filter(
    c => c && c.trim() !== ""
  );

  const saveAlert = () => {
    if (!crop) {
      alert("Please select a crop");
      return;
    }

    const alertData = {
      crop,
      minPrice,
      maxPrice
    };

    localStorage.setItem(
      "priceAlert",
      JSON.stringify(alertData)
    );

    alert("âœ… Price alert saved");
  };

  return (
    <div className="alert-card">
      <h3>ðŸ”” Price Alert Settings</h3>

      <select
        value={crop}
        onChange={e => setCrop(e.target.value)}
      >
        <option value="">ðŸŒ¾ Select Crop</option>

        {cleanCrops.map((c, i) => (
          <option key={i} value={c}>
            {c}
          </option>
        ))}
      </select>

      <input
        type="number"
        placeholder="Notify if price below â‚¹"
        value={minPrice}
        onChange={e => setMinPrice(e.target.value)}
      />

      <input
        type="number"
        placeholder="Notify if price above â‚¹"
        value={maxPrice}
        onChange={e => setMaxPrice(e.target.value)}
      />

      <button onClick={saveAlert}>
        ðŸ”” Save Alert
      </button>
    </div>
  );
}

export default PriceAlertSettings;
