export function speakText(text) {
  if (!("speechSynthesis" in window)) {
    alert("Voice not supported");
    return;
  }

  const msg = new SpeechSynthesisUtterance(text);
  msg.lang = "en-IN";
  msg.rate = 0.9;

  window.speechSynthesis.cancel();
  window.speechSynthesis.speak(msg);
}
