import { productGroups } from "../utils/productGroups";

function ProductGroups({ crops = [] }) {
  const grouped = {};

  crops.forEach(crop => {
    let found = false;

    for (const group in productGroups) {
      if (productGroups[group].includes(crop)) {
        if (!grouped[group]) grouped[group] = [];
        grouped[group].push(crop);
        found = true;
        break;
      }
    }

    if (!found) {
      if (!grouped.Others) grouped.Others = [];
      grouped.Others.push(crop);
    }
  });

  return (
    <div style={{ padding: "20px" }}>
      {Object.keys(grouped).map(group => (
        <div key={group} style={{ marginBottom: "25px" }}>
          <h3>ðŸ“¦ {group}</h3>

          <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
            {grouped[group].map((item, i) => (
              <span
                key={i}
                style={{
                  padding: "8px 14px",
                  border: "1px solid #ccc",
                  borderRadius: "20px",
                  background: "#f9f9f9"
                }}
              >
                {item}
              </span>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default ProductGroups;
