import { useEffect, useState } from "react";
import DashboardCards from "../components/DashboardCards";
import CategoryList from "../components/CategoryList";
import TopBestCrops from "../components/TopBestCrops";
import { speakBestMarket } from "../utils/speakPrice";

function Dashboard() {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/data")
      .then(res => res.json())
      .then(json => {
        setData(json);
      });
  }, []);

  // ðŸ”Š MANUAL VOICE (Browser safe)
  const handleSpeak = () => {
    if (!data.length) return;

    // ðŸ•’ Greeting
    const hour = new Date().getHours();
    let greeting = "Good evening";
    if (hour < 12) greeting = "Good morning";
    else if (hour < 17) greeting = "Good afternoon";

    // ðŸ‘¤ USER NAME
    const user =
      JSON.parse(localStorage.getItem("user")) || {};
    const userName = user.name || "Farmer";

    // â­ BEST MARKET
    const best = data.reduce((max, cur) =>
      Number(cur.Modal_x0020_Price) >
      Number(max.Modal_x0020_Price)
        ? cur
        : max
    );

    const crop =
      best.Commodity ||
      best.commodity ||
      best.Crop ||
      best.crop_name;

    // ðŸ”Š SPEAK
    speakBestMarket({
      greeting,
      userName,
      crop,
      market: best.Market,
      state: best.State,
      price: best.Modal_x0020_Price
    });
  };

  return (
    <div>
      {/* ðŸ”Š VOICE BUTTON */}
      <div style={{ padding: "15px" }}>
        <button
          onClick={handleSpeak}
          style={{
            padding: "12px 20px",
            background: "#2e7d32",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer"
          }}
        >
          ðŸ”Š Hear Todayâ€™s Best Market
        </button>
      </div>

      <DashboardCards
        data={data}
        onCategoryClick={setSelectedCategory}
      />

      <TopBestCrops data={data} />

      {selectedCategory && (
        <CategoryList
          data={data}
          category={selectedCategory}
        />
      )}
    </div>
  );
}

export default Dashboard;
