import { useEffect, useState, useRef } from "react";
import DashboardCards from "../components/DashboardCards";
import CategoryList from "../components/CategoryList";
import TopBestCrops from "../components/TopBestCrops";
import { speakBestMarket } from "../utils/speakPrice";

function Dashboard() {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  // ðŸ”Š Speak only ONCE
  const hasSpoken = useRef(false);

  // ðŸ”„ LOAD DATA + SAVE ALL CROPS + AUTO SPEAK
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/data")
      .then(res => res.json())
      .then(json => {
        // âœ… Save full data
        setData(json);

        // âœ… Extract all unique crop names
        const allCrops = [
          ...new Set(
            json
              .map(
                d =>
                  d.Commodity ||
                  d.commodity ||
                  d.Crop ||
                  d.crop_name
              )
              .filter(Boolean)
          )
        ];

        // âœ… Save crops for alerts
        localStorage.setItem(
          "allCrops",
          JSON.stringify(allCrops)
        );

        // ðŸ”¥ AUTO SPEAK BEST MARKET (ONLY ONCE)
        if (json.length && !hasSpoken.current) {
          const best = json.reduce((max, cur) =>
            Number(cur.Modal_x0020_Price) >
            Number(max.Modal_x0020_Price)
              ? cur
              : max
          );

          const crop =
            best.Commodity ||
            best.commodity ||
            best.Crop ||
            best.crop_name;

          speakBestMarket({
            crop,
            market: best.Market,
            state: best.State,
            price: best.Modal_x0020_Price
          });

          hasSpoken.current = true;
        }
      })
      .catch(err => {
        console.error("Error fetching data:", err);
      });
  }, []);

  return (
    <div>
      {/* CATEGORY CARDS */}
      <DashboardCards
        data={data}
        onCategoryClick={category =>
          setSelectedCategory(category)
        }
      />

      {/* TOP BEST CROPS */}
      <TopBestCrops data={data} />

      {/* CATEGORY DETAILS */}
      {selectedCategory && (
        <CategoryList
          data={data}
          category={selectedCategory}
        />
      )}
    </div>
  );
}

export default Dashboard;
