export function speakPrice({
  crop,
  market,
  price,
  state,
  lang = "en"
}) {
  if (!window.speechSynthesis) {
    alert("Voice not supported in this browser");
    return;
  }

  let message = "";

  if (lang === "kn") {
    message = `${crop} ಬೆಲೆ ಇಂದು ${market} ಮಾರುಕಟ್ಟೆಯಲ್ಲಿ ${price} ರೂಪಾಯಿ ಇದೆ`;
  } else {
    message = `Today ${crop} price in ${market} market is ${price} rupees`;
  }

  const utterance = new SpeechSynthesisUtterance(message);

  const voices = window.speechSynthesis.getVoices();

  // ✅ FORCE KANNADA VOICE
  if (lang === "kn") {
    const kannadaVoice = voices.find(v =>
      v.lang.toLowerCase().includes("kn")
    );

    if (kannadaVoice) {
      utterance.voice = kannadaVoice;
      utterance.lang = "kn-IN";
    } else {
      alert("Kannada voice not available in this browser");
      return;
    }
  } else {
    utterance.lang = "en-IN";
  }

  utterance.rate = 0.85;
  utterance.pitch = 1;
  utterance.volume = 1;

  window.speechSynthesis.cancel();
  window.speechSynthesis.speak(utterance);
}
