import "./DashboardCards.css";

function DashboardCards({ data = [], onCategoryClick }) {
  // safety check
  if (!onCategoryClick) return null;

  const vegetables = data.filter(d => d.category === "Vegetables").length;
  const fruits = data.filter(d => d.category === "Fruits").length;
  const others = data.length - vegetables - fruits;

  return (
    <div className="dashboard-wrapper">
      <h2 className="dashboard-title">üåæ Crop Dashboard</h2>

      <div className="card-container">
        <div
          className="card green"
          onClick={() => onCategoryClick("Vegetables")}
        >
          <h3>ü•¶ Vegetables</h3>
          <h1>{vegetables}</h1>
          <p>Crops</p>
        </div>

        <div
          className="card orange"
          onClick={() => onCategoryClick("Fruits")}
        >
          <h3>üçé Fruits</h3>
          <h1>{fruits}</h1>
          <p>Crops</p>
        </div>

        <div
          className="card blue"
          onClick={() => onCategoryClick("Others")}
        >
          <h3>üåæ Others</h3>
          <h1>{others}</h1>
          <p>Crops</p>
        </div>
      </div>
    </div>
  );
}

export default DashboardCards;
